import React,{Component} from 'react'; 
import NavBar from "./components/NavBar";
import DisplayImage from "./components/DisplayImage";
import Result from "./components/Result";
class App extends Component { 
    constructor(props){
      super(props);
      this.state={
        imageUrl:""
      }
    }

    preview = (e) => {

        const file    = this.refs.uploadImg.files[0]
        const reader  = new FileReader();

        reader.onloadend = () => {
            this.setState({
                imageUrl: reader.result
            })
        }
        if (file) {
            reader.readAsDataURL(file);
            this.setState({
                imageUrl :reader.result
            })
        } 
        else {
            this.setState({
                imageUrl: ""
            })
        }
    }

    render() { 

      return ( 
        <div>
          <NavBar/>
          <div class="upload">
            <form action="http://localhost:8080/classify" method="post" enctype="multipart/form-data">
              <input type="file" accept="image/*" name="photo"></input>
              <input type="submit" value="upload"></input>
            </form>

            <form>      
              <input
                ref="uploadImg"
                type="file"
                name="selectedFile"
                accept="image/*"
                onChange={this.preview}
                />
            </form>
          </div>
          <DisplayImage path={this.state.imageUrl}/>
          <Result name="hello"/>
        </div>    
      )
    } 
  } 

export default App;
