import React,{Component} from 'react'; 
import NavBar from "./components/NavBar";
import DisplayImage from "./components/DisplayImage";
import Result from "./components/Result";
const axios = require("axios");

class App extends Component { 
    constructor(props){
      super(props);
      this.state={
        imageUrl:"",
        file: null
      }
      this.onFormSubmit = this.onFormSubmit.bind(this);
    }

    preview = (e) => {

        const file    = this.refs.uploadImg.files[0]
        const reader  = new FileReader();

        reader.onloadend = () => {
            this.setState({
                imageUrl: reader.result
            })
        }
        if (file) {
            reader.readAsDataURL(file);
            this.setState({
                imageUrl :reader.result
            })
        } 
        else {
            this.setState({
                imageUrl: ""
            })
        }
    }

    onFormSubmit(e){
      e.preventDefault();
      const formData = new FormData();
      formData.append('myImage',this.state.file);
      const config = {
          headers: {
              'content-type': 'multipart/form-data'
          }
      };
      axios.post("/upload",formData,config)
          .then((response) => {
              alert("The file is successfully uploaded");
          }).catch((error) => {
      });
    }


    render() { 

      return ( 
        <div>
          <NavBar/>
          <div class="upload">
            <form action="http://localhost:8080/classify" method="post" enctype="multipart/form-data">
              <input type="file" accept="image/*" name="photo"></input>
              <input type="submit" value="upload"></input>
            </form>

            <form onSubmit={this.onFormSubmit}>      
              <input
                ref="uploadImg"
                type="file"
                name="selectedFile"
                accept="image/*"
                onChange={this.preview}
                />
                <input type="submit" name="myImage"></input>
            </form>
          </div>
          <DisplayImage path={this.state.imageUrl}/>
          <Result name="hello"/>
        </div>    
      )
    } 
  } 

export default App;
